insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('47289ff3-d59a-448f-b352-4d05a1eae261','为你自己读书','肖卫',26.8,100,'5ae6b948-99d1-41c6-affa-bdfce4790584','e1df4a98-90ce-4761-bd05-95dffdb44c98','99924e92-4257-4cf2-8f76-8a1546d9963b','讲述了读书不仅能帮助一个人开拓前程，而且能帮助一个人成就事业。读书能使人聪明、智慧，并且能使人谦虚、自信，有耐心和机智，而这些都是未来成功必须要有的要素。日积月累的读书生活，是明天事业成功的关键。没有今天的勤奋渎书作为人生的保证，何来将来的辉煌业绩？ 读书的真正目的，实质是为给自己创造一个美好的未来、实现自己的人生梦想，因此，每个人都是在为自己读书');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('db8f7435-aeee-4f31-87fa-75b14ba8200a','猎人笔记','屠格涅夫',88,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','这是一部通过猎人的狩猎活动，记述十九世纪中叶俄罗斯农村生活的随笔集。随着屠格涅夫充满优美调的叙述，俄罗斯的大自然风光、俄罗斯人民的风俗习惯、地主对农民的欺凌、农民的善良淳朴和智慧，像一首首抒怀歌曲在我们面前缓缓流淌出来，汇成一部色彩斑斓、动人心魄的交响诗。对二十一世纪的读者来说，它仍是一部给人以无限美好的艺术享受和富有教益的不巧之作');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('7ea6bb01-e24c-46fe-af36-a456a636fcfe','活着','余华',21.8,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','《活着》是作家余华的代表作之一，讲诉了在大时代背景下，随着内战、三反五反，大跃进，文化大革命等社会变革，徐福贵的人生和家庭不断经受着苦难，到了最后所有亲人都先后离他而去，仅剩下年老的他和一头老牛相依为命');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('8d7cf812-b7fa-4ebf-8083-c69b534895c3','安徒生童话','安徒生',35,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','《安徒生童话》共由163篇故事组成。该作爱憎分明，热情歌颂劳动人民、赞美他们的善良和纯洁的优秀品德；无情地揭露和批判王公贵族们的愚蠢、无能、贪婪和残暴。其中较为闻名的故事有：《小人鱼》、《丑小鸭》、《卖火柴的小女孩》、《拇指姑娘》等');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('859016d1-4b36-4013-a8ed-afbb04e42d31','人月神话','布鲁克斯',50,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','《人月神话》内容源于作者Brooks在IBM公司任System计算机系列以及其庞大的软件系统OS项目经理时的实践经验');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('b6f4397e-0243-4912-8003-674deacdb691','腾讯游戏设计创意谈','腾讯ieg市场部创意设计中心',61,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','TGideas 作为腾讯游戏的专业创意设计团队,将他们数十个实战案例的精彩创意和营销形态,按照游戏的生命周期汇总成了一本书:《Ideas for Game 腾讯游戏创意设计谈》');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('229a49b5-8c01-4812-9a8b-a12239ef18f7','将来的你一定会感谢现在拼命的自己','黎芫',38,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','这是一部激励千万心灵的年度暖心之作，写给无数次跌倒却依然默默努力的年轻人，写给为了心中的梦想而决不放弃的年轻人。');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('47b9451-cfe0-4aea-a304-decb3cd0d835','假如给我三天光明','海伦凯勒',39.8,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','该书的前半部分主要写了海伦变成盲聋人后的生活，后半部分则介绍了海伦的求学生涯。同时也介绍她体会不同的丰富多彩的生活以及她的慈善活动等等');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('99827e5a-75a1-4e7b-ac8c-335aee4d279f','星星离我们有多远','卞毓麟',48,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','天文学是一门奥妙无穷，令人神往的学科。作者把历代天文学家创造“量天尺”的过程娓娓道来，介绍了从近处的月亮到极远处的类星体的距离的量、估，包含了大量的天文知识和历史知识。作品文笔流畅，故事性强，是难得的天文科普佳作');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('42d81354-0ff3-4b0d-bc7a-806d998ae708','贝尔写给你的荒野求生','贝尔格里尔斯',76,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','本书是英国童子军总会的总领袖贝尔专门为童子军创作的野外活动指南。书中详细介绍了各种野外求生技巧，从露营方法、觅食方法到野外救援，从装备选择、工具制作到自然观察等一应俱全，不仅能教孩子们从容应对野外各种困难，更能让他们学会动手、学会生存');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('d7a9e39a-3516-47e1-988a-3f7365acf7a0','情商高就是说话让人舒服','朱凌',22.8,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','《情商高，就是说话让人舒服》以“说话让人舒服”为主题，将语言和人性有效地结合起来，通过大量贴近生活的事例和精炼的要点，教会我们，如何通过洞察别人的心里来说出让人爱听的话，如何通过体察别人的需要来说出让人开心的话，如何通过“包装”自己的语言来说出不伤人的话……让你的每一句话都说得得体又令人舒服，到哪都受欢迎');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('5b8db507-333b-4ca2-b46e-b9eb334e1ce7','静下心来，找回自己','慧闻',19.9,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','有烦心事时,先多往好处想想。 慢下来,才能找到生命的美。 练习静下来是修行,能静下来是福气');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('8c667fd2-6ae7-4fb9-a9d5-bbb72993ae7b','人间失格','太宰治',12.5,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','作品中太宰治巧妙地将自己的人生与思想，隐藏于主角叶藏的人生遭遇，藉由叶藏的独白，窥探太宰治的内心世界――“充满了可耻的一生”。在发表该作品的同年，太宰治自杀身亡');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('132fac16-2657-494b-8e73-22e58abea189','白夜行','东野圭吾',45.9,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','小说将无望却坚守的凄凉爱情和执著而缜密的冷静推理完美结合。2006年，小说被改编成同名电视连续剧，一举囊括第48届日剧学院赏四项大奖');
insert into book (id,name,author,price,total,big_book_type_id,small_book_type_id,publisher_id,sumarry) values('a83b8049-9d87-4abb-ad94-60ac21c1037e','基督山伯爵','大仲马',11.8,100,'2bcdcf2f-1787-44cb-a39f-9d666bf5f4c6',null,'5b9bef3d-cc5f-45db-ad34-12222c1ff6dd','《基督山伯爵》是通俗历史小说，法国著名作家大仲马（1802-1870）的代表作。故事讲述19世纪法国皇帝拿破仑“百日王朝”时期，法老号大副爱德蒙・邓蒂斯受船长委托，为拿破仑党人送了一封信，遭到两个卑鄙小人和法官的陷害，被打入黑牢。狱友法利亚神甫向他传授各种知识，并在临终前把埋于基督山岛上的一批宝藏的秘密告诉了他。唐泰斯越狱后找到了宝藏，成为巨富，从此化名基督山伯爵（水手森巴），经过精心策划，报答了恩人，惩罚了仇人。充满传奇色彩，奇特新颖，引人入胜');
